import './App.css';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Home from './pages/Home';
import New from './pages/New';
import Diary from './pages/Diary';
import Edit from './pages/Edit';
import { useReducer, useRef } from 'react';

const reducer = (state, action) => {
  let newState = [];
  switch (action.type) {
    case 'INIT': {
      return action.data;
    }
    case 'CREATE': {
      const newItem = {
        ...action.data,
      };
      newState = [newItem, ...state];
      break;
    }
    case 'REMOVE': {
      newState = state.filter((it) => it.id !== action.targetId);
      break;
    }
    case 'EDIT': {
      newState = state.map((it) => (it.id === action.data.id ? { ...action.data } : it));
      break;
    }
    default:
      return state;
  }
  return newState;
};

//components

function App() {
  const [data, dispatch] = useReducer(reducer, []);
  const dataId = useRef(0);
  //CREATE
  const onCreate = (date, content, exercise) => {
    dispatch({
      type: 'CREATE',
      data: {
        id: dataId.current,
        date: new Date(date).getTime(),
        content,
        exercise,
      },
    });
  };

  return (
    <BrowserRouter>
      <div className='App'>
        {/* <img src={process.env.PUBLIC_URL + '/assets/muscles.png'} style={{ width: 150 }}></img>
        <img src={process.env.PUBLIC_URL + '/assets/healthy.png'} style={{ width: 150 }}></img>
        <img src={process.env.PUBLIC_URL + '/assets/weight.png'} style={{ width: 150 }}></img>
        <img src={process.env.PUBLIC_URL + '/assets/weakness.png'} style={{ width: 150 }}></img>
        <img src={process.env.PUBLIC_URL + '/assets/belly.png'} style={{ width: 150 }}></img> */}
        <Routes>
          <Route path='/' element={<Home />} />
          <Route path='/new' element={<New />} />
          <Route path='/diary/:id' element={<Diary />} />
          <Route path='/edit' element={<Edit />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
